                <ul class="footer-info-list">
                    {{#each categories}}
                        <li>
                            <a href="{{url}}">{{name}}</a>
                        </li>
                    {{/each}}
                    {{#if settings.gift_certificates_enabled}}
                        <li>
                            <a href="{{urls.gift_certificate.purchase}}">{{lang 'common.gift_cert'}}</a>
                        </li>
                    {{/if}}
                </ul>
            </article>

            <article class="footer-info-col footer-info-dropdown footer-info-col--small" data-section-type="footer-webPages">
                <h5 class="footer-info-heading">
                    {{lang 'footer.navigate'}}
                    <i class="icon" aria-hidden="true">
                        <svg><use xlink:href="#icon-downselector" /></svg>
                    </i>
                </h5>
                <ul class="footer-info-list">
                    {{#each pages}}
                        <li>
                            <a href="{{url}}">{{name}}</a>
                        </li>
                    {{/each}}
                    <li>
                        <a href="{{urls.sitemap}}">{{lang 'sitemap.page_title'}}</a>
                    </li>
                </ul>
            </article>

            {{#and theme_settings.shop_by_brand_show_footer shop_by_brand.length}}
                <article class="footer-info-col footer-info-dropdown footer-info-col--small" data-section-type="footer-brands">
                    <h5 class="footer-info-heading">
                        {{lang 'footer.brands'}}
                        <i class="icon" aria-hidden="true">
                            <svg><use xlink:href="#icon-downselector" /></svg>
                        </i>
                    </h5>
                    <ul class="footer-info-list">
                        {{#each shop_by_brand}}
                            <li>
                                <a href="{{url}}">{{name}}</a>
                            </li>
                        {{/each}}
                        <li><a href="{{urls.brands}}">{{lang 'common.view_all'}}</a></li>
                    </ul>
                </article>
            {{/and}}

            {{#if theme_settings.social_icons_footer}}
                <article class="footer-info-col footer-mobile-limited footer-info-col--small" data-section-type="footer-webPages">
                    <h5 class="footer-info-heading">{{lang 'social.connect'}}</h5>
                    {{> components/common/social-links}}
                </article>
            {{/if}}

            {{#if settings.address}}
                <article class="footer-info-col footer-mobile-limited footer-info-col--small" data-section-type="storeInfo">
                    <h5 class="footer-info-heading footer-store-name">{{settings.store_name}}</h5>
                    <address>{{nl2br settings.address}}</address>
                    {{#if settings.phone_number}}
                        <div class="footer-info-phone">
                            <i class="icon" aria-hidden="true">
                                <svg><use xlink:href="#icon-phone" /></svg>
                            </i>
                            <a href="tel:{{settings.phone_number}}">{{settings.phone_number}}</a>
                        </div>
                    {{/if}}
                </article>
            {{/if}}
        </section>
        <div class="footer-copyright">
            {{> components/common/payment-icons}}
            {{#if theme_settings.show_copyright_footer}}
                <p class="powered-by">&copy; {{moment format="YYYY"}} {{settings.store_name}} </p>
            {{/if}}
        </div>

        {{#or theme_settings.show_powered_by theme_settings.show_built_by}}
            <div class="footer-copyright" style="font-size: 12px;">
                {{#if theme_settings.show_powered_by}}
                    <p class="powered-by">Powered by <a href="https://www.bigcommerce.com?utm_source=merchant&amp;utm_medium=poweredbyBC" rel="nofollow">BigCommerce</a></p>
                {{/if}}
                {{#if theme_settings.show_built_by}}
                    <p class="powered-by">Theme by <a href="https://www.weizenyoung.com/" rel="nofollow">Weizen Young</a></p>
                {{/if}}
            </div>
        {{/or}}
    </div>
    {{{region name="ssl_site_seal--global"}}}
</footer>

